import React, { useState } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Box,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  Divider,
  useTheme,
  alpha,
  Chip,
  Avatar,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Tooltip,
  Button,
  TextField,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Drawer,
  ListItemIcon,
  TablePagination,
  Alert,
  ListItemButton,
} from '@mui/material';
import {
  People as PeopleIcon,
  LocalHospital as VaccinationIcon,
  Assessment as AssessmentIcon,
  Warning as WarningIcon,
  School as SchoolIcon,
  CalendarToday as CalendarIcon,
  Edit as EditIcon,
  Visibility as ViewIcon,
  Phone as PhoneIcon,
  Email as EmailIcon,
  LocationOn as LocationIcon,
  Add as AddIcon,
  FilterList as FilterIcon,
  Search as SearchIcon,
  Group as GroupIcon,
  Dashboard as DashboardIcon,
  PersonAdd as PersonAddIcon,
  FileUpload as FileUploadIcon,
  Description as ReportIcon,
  Event as EventIcon,
  Download as DownloadIcon,
} from '@mui/icons-material';

// Sample data
const dashboardData = {
  totalStudents: 1250,
  vaccinatedStudents: 980,
  upcomingDrives: [
    { id: 1, name: 'COVID-19 Booster', date: '2024-03-15', location: 'Main Auditorium' },
    { id: 2, name: 'BCG Vaccine', date: '2024-03-20', location: 'School Health Center' },
    { id: 3, name: 'DPT Booster', date: '2024-03-25', location: 'Main Auditorium' },
  ],
  recentVaccinations: [
    { 
      id: 1, 
      student: 'Arjun Sharma',
      class: 'Class 10-A',
      age: 15,
      rollNumber: '2024A001',
      vaccine: 'COVID-19 Booster',
      date: '2024-03-01',
      status: 'Completed',
      nextDue: '2025-03-01'
    },
    { 
      id: 2, 
      student: 'Priya Patel',
      class: 'Class 9-B',
      age: 14,
      rollNumber: '2024B002',
      vaccine: 'BCG',
      date: '2024-03-02',
      status: 'Completed',
      nextDue: 'N/A'
    },
    { 
      id: 3, 
      student: 'Rahul Gupta',
      class: 'Class 11-C',
      age: 16,
      rollNumber: '2024C003',
      vaccine: 'DPT',
      date: '2024-03-03',
      status: 'Completed',
      nextDue: '2025-03-03'
    },
    { 
      id: 4, 
      student: 'Ananya Singh',
      class: 'Class 8-A',
      age: 13,
      rollNumber: '2024A004',
      vaccine: 'MMR',
      date: '2024-03-04',
      status: 'Completed',
      nextDue: 'N/A'
    },
    { 
      id: 5, 
      student: 'Vikram Mehta',
      class: 'Class 12-B',
      age: 17,
      rollNumber: '2024B005',
      vaccine: 'COVID-19 Booster',
      date: '2024-03-05',
      status: 'Completed',
      nextDue: '2025-03-05'
    },
  ],
  statistics: {
    vaccinationRate: 78.4,
    pendingVaccinations: 270,
    upcomingAppointments: 45,
    classDistribution: {
      'Class 8': 180,
      'Class 9': 220,
      'Class 10': 250,
      'Class 11': 200,
      'Class 12': 200,
    }
  }
};

// Student data
const studentData = {
  students: [
    {
      id: 1,
      name: 'Arjun Sharma',
      class: 'Class 10-A',
      age: 15,
      rollNumber: '2024A001',
      gender: 'Male',
      bloodGroup: 'O+',
      contact: {
        phone: '+91 98765 43210',
        email: 'arjun.sharma@school.com',
        address: '123, Green Park, New Delhi'
      },
      vaccinations: [
        {
          name: 'COVID-19 Booster',
          date: '2024-03-01',
          status: 'Completed',
          nextDue: '2025-03-01',
          batch: 'COV-2024-001'
        },
        {
          name: 'BCG',
          date: '2010-01-15',
          status: 'Completed',
          nextDue: 'N/A',
          batch: 'BCG-2010-001'
        },
        {
          name: 'DPT',
          date: '2023-06-15',
          status: 'Completed',
          nextDue: '2024-06-15',
          batch: 'DPT-2023-001'
        }
      ],
      medicalHistory: [
        {
          condition: 'Asthma',
          diagnosed: '2018',
          status: 'Under Control',
          notes: 'Regular check-ups required'
        }
      ]
    },
    {
      id: 2,
      name: 'Priya Patel',
      class: 'Class 9-B',
      age: 14,
      rollNumber: '2024B002',
      gender: 'Female',
      bloodGroup: 'B+',
      contact: {
        phone: '+91 98765 43211',
        email: 'priya.patel@school.com',
        address: '456, Sector 15, Gurgaon'
      },
      vaccinations: [
        {
          name: 'BCG',
          date: '2024-03-02',
          status: 'Completed',
          nextDue: 'N/A',
          batch: 'BCG-2024-001'
        },
        {
          name: 'MMR',
          date: '2023-08-20',
          status: 'Completed',
          nextDue: 'N/A',
          batch: 'MMR-2023-001'
        }
      ],
      medicalHistory: []
    },
    {
      id: 3,
      name: 'Rahul Gupta',
      class: 'Class 11-C',
      age: 16,
      rollNumber: '2024C003',
      gender: 'Male',
      bloodGroup: 'A+',
      contact: {
        phone: '+91 98765 43212',
        email: 'rahul.gupta@school.com',
        address: '789, Model Town, Delhi'
      },
      vaccinations: [
        {
          name: 'DPT',
          date: '2024-03-03',
          status: 'Completed',
          nextDue: '2025-03-03',
          batch: 'DPT-2024-001'
        },
        {
          name: 'COVID-19 Booster',
          date: '2023-12-15',
          status: 'Completed',
          nextDue: '2024-12-15',
          batch: 'COV-2023-001'
        }
      ],
      medicalHistory: []
    }
  ]
};

// Student Management Data
const studentManagementData = {
  classes: [
    { id: 1, name: 'Class 8', sections: ['A', 'B', 'C'] },
    { id: 2, name: 'Class 9', sections: ['A', 'B', 'C'] },
    { id: 3, name: 'Class 10', sections: ['A', 'B', 'C'] },
    { id: 4, name: 'Class 11', sections: ['A', 'B', 'C'] },
    { id: 5, name: 'Class 12', sections: ['A', 'B', 'C'] },
  ],
  vaccinationStatus: {
    pending: 270,
    completed: 980,
    upcoming: 45,
    overdue: 12,
  },
  classDistribution: {
    'Class 8': { total: 180, vaccinated: 150, pending: 30 },
    'Class 9': { total: 220, vaccinated: 180, pending: 40 },
    'Class 10': { total: 250, vaccinated: 200, pending: 50 },
    'Class 11': { total: 200, vaccinated: 150, pending: 50 },
    'Class 12': { total: 200, vaccinated: 150, pending: 50 },
  },
  recentRegistrations: [
    {
      id: 1,
      name: 'Neha Verma',
      class: 'Class 8-A',
      date: '2024-03-10',
      status: 'Pending Vaccination',
    },
    {
      id: 2,
      name: 'Rohan Kapoor',
      class: 'Class 9-B',
      date: '2024-03-09',
      status: 'Vaccination Complete',
    },
    {
      id: 3,
      name: 'Sanya Malhotra',
      class: 'Class 10-C',
      date: '2024-03-08',
      status: 'Pending Documentation',
    },
  ],
  vaccinationDrives: [
    {
      id: 1,
      name: 'COVID-19 Booster',
      date: '2024-03-15',
      location: 'Main Auditorium',
      status: 'Upcoming',
      totalSlots: 100,
      registeredStudents: 75,
      vaccineType: 'COVID-19',
      batchNumber: 'COV-2024-001',
      requirements: ['Previous COVID-19 vaccination record', 'Parent consent form'],
      notes: 'Booster dose for students above 12 years'
    },
    {
      id: 2,
      name: 'BCG Vaccine',
      date: '2024-03-20',
      location: 'School Health Center',
      status: 'Upcoming',
      totalSlots: 50,
      registeredStudents: 30,
      vaccineType: 'BCG',
      batchNumber: 'BCG-2024-001',
      requirements: ['Medical history', 'Parent consent form'],
      notes: 'For new admissions'
    },
    {
      id: 3,
      name: 'DPT Booster',
      date: '2024-03-25',
      location: 'Main Auditorium',
      status: 'Upcoming',
      totalSlots: 80,
      registeredStudents: 45,
      vaccineType: 'DPT',
      batchNumber: 'DPT-2024-001',
      requirements: ['Previous DPT record', 'Parent consent form'],
      notes: 'Booster dose for Class 10 students'
    }
  ]
};

function StatCard({ title, value, icon, color }) {
  const theme = useTheme();
  
  return (
    <Card 
      sx={{ 
        height: '100%',
        transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: theme.shadows[8],
        },
        background: `linear-gradient(135deg, ${alpha(color, 0.1)} 0%, ${alpha(color, 0.05)} 100%)`,
        border: `1px solid ${alpha(color, 0.2)}`,
      }}
    >
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box
            sx={{
              backgroundColor: alpha(color, 0.1),
              borderRadius: '50%',
              p: 1,
              mr: 2,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            {React.cloneElement(icon, { sx: { color, fontSize: 28 } })}
          </Box>
          <Typography variant="h6" component="div" sx={{ color: theme.palette.text.secondary }}>
            {title}
          </Typography>
        </Box>
        <Typography 
          variant="h4" 
          component="div" 
          sx={{ 
            color,
            fontWeight: 'bold',
            textAlign: 'center',
            mb: 1,
          }}
        >
          {value}
        </Typography>
      </CardContent>
    </Card>
  );
}

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

function StudentDetails({ student }) {
