          <Tab label="Students" />
          <Tab label="Vaccination Drives" />
        </Tabs>
      </Box>

      <TabPanel value={activeTab} index={0}>
        {/* Existing student management content */}
      </TabPanel>

      <TabPanel value={activeTab} index={1}>
        <VaccinationDriveManagement />
      </TabPanel>
    </Box>
  );
}

function ReportGeneration() {
  const theme = useTheme();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [selectedVaccine, setSelectedVaccine] = useState('all');
  const [selectedStatus, setSelectedStatus] = useState('all');

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleDownload = (format) => {
    // Implement download functionality
    console.log(`Downloading report in ${format} format`);
  };

  return (
    <Box>
      <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Box>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>
            Vaccination Reports
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Generate and download vaccination reports
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button
            variant="outlined"
            startIcon={<DownloadIcon />}
            onClick={() => handleDownload('csv')}
          >
            Download CSV
          </Button>
          <Button
            variant="outlined"
            startIcon={<DownloadIcon />}
            onClick={() => handleDownload('pdf')}
          >
            Download PDF
          </Button>
        </Box>
      </Box>

      <Paper sx={{ p: 3, mb: 3 }}>
        <Grid container spacing={2} sx={{ mb: 3 }}>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Vaccine Type</InputLabel>
              <Select
                value={selectedVaccine}
                label="Vaccine Type"
                onChange={(e) => setSelectedVaccine(e.target.value)}
              >
                <MenuItem value="all">All Vaccines</MenuItem>
                <MenuItem value="covid">COVID-19</MenuItem>
                <MenuItem value="bcg">BCG</MenuItem>
                <MenuItem value="dpt">DPT</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Status</InputLabel>
              <Select
                value={selectedStatus}
                label="Status"
                onChange={(e) => setSelectedStatus(e.target.value)}
              >
                <MenuItem value="all">All Status</MenuItem>
                <MenuItem value="completed">Completed</MenuItem>
                <MenuItem value="pending">Pending</MenuItem>
                <MenuItem value="upcoming">Upcoming</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label="Search"
              variant="outlined"
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'text.secondary' }} />,
              }}
            />
          </Grid>
        </Grid>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Student Name</TableCell>
                <TableCell>Class</TableCell>
                <TableCell>Vaccine</TableCell>
                <TableCell>Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Next Due</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {dashboardData.recentVaccinations
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((vaccination) => (
                  <TableRow key={vaccination.id}>
                    <TableCell>{vaccination.student}</TableCell>
                    <TableCell>{vaccination.class}</TableCell>
                    <TableCell>{vaccination.vaccine}</TableCell>
                    <TableCell>{new Date(vaccination.date).toLocaleDateString()}</TableCell>
                    <TableCell>
                      <Chip
                        label={vaccination.status}
                        color="success"
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      {vaccination.nextDue === 'N/A' ? 'N/A' : new Date(vaccination.nextDue).toLocaleDateString()}
                    </TableCell>
                  </TableRow>
                ))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={dashboardData.recentVaccinations.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Paper>
    </Box>
  );
}

function BulkUploadDialog({ open, onClose }) {
