  };

  const handleUpload = () => {
    // Implement bulk upload functionality
    console.log('Uploading file:', file);
    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
      <DialogTitle>Bulk Upload Students</DialogTitle>
      <DialogContent>
        <Box sx={{ mt: 2 }}>
          <Typography variant="body2" sx={{ mb: 2 }}>
            Upload a CSV file containing student details. The file should include the following columns:
            Full Name, Class, Section, Roll Number, Date of Birth, Blood Group, Contact Number, Email, Address
          </Typography>
          <Button
            variant="outlined"
            component="label"
            fullWidth
            startIcon={<FileUploadIcon />}
          >
            Choose File
            <input
              type="file"
              hidden
              accept=".csv"
              onChange={handleFileChange}
            />
          </Button>
          {file && (
            <Typography variant="body2" sx={{ mt: 1 }}>
              Selected file: {file.name}
            </Typography>
          )}
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button
          variant="contained"
          onClick={handleUpload}
          disabled={!file}
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
          }}
        >
          Upload
        </Button>
      </DialogActions>
    </Dialog>
  );
}

function Dashboard() {
  const theme = useTheme();
  const [tabValue, setTabValue] = useState(0);
  const [openBulkUpload, setOpenBulkUpload] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(true);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const menuItems = [
    { text: 'Dashboard', icon: <DashboardIcon />, value: 0 },
    { text: 'Students', icon: <PeopleIcon />, value: 1 },
    { text: 'Vaccination Drives', icon: <EventIcon />, value: 2 },
    { text: 'Reports', icon: <ReportIcon />, value: 3 },
  ];

  return (
    <Box sx={{ display: 'flex' }}>
      <Drawer
        variant="permanent"
        sx={{
          width: 240,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: 240,
            boxSizing: 'border-box',
            background: `linear-gradient(180deg, ${alpha(theme.palette.primary.main, 0.05)} 0%, ${alpha(theme.palette.primary.main, 0.02)} 100%)`,
          },
        }}
      >
        <Box sx={{ p: 2, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 0.5, textAlign: 'center', letterSpacing: 1 }}>
            School Vaccination
          </Typography>
          <Typography variant="caption" sx={{ color: 'text.secondary', textAlign: 'center' }}>
            Tracker
          </Typography>
        </Box>
        <Divider />
        <List>
          {menuItems.map((item) => (
            <ListItem key={item.text} disablePadding>
              <ListItemButton
                selected={tabValue === item.value}
                onClick={() => setTabValue(item.value)}
                sx={{
                  pl: 3,
                  py: 1.5,
                  '&.Mui-selected': {
                    background: `linear-gradient(90deg, ${alpha(theme.palette.primary.main, 0.08)}, transparent)`
                  }
                }}
              >
                <ListItemIcon sx={{ minWidth: 36 }}>{item.icon}</ListItemIcon>
                <ListItemText primary={item.text} primaryTypographyProps={{ sx: { fontWeight: 500 } }} />
              </ListItemButton>
            </ListItem>
          ))}
        </List>
      </Drawer>

      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <Container maxWidth="lg">
          {tabValue === 0 && (
            <>
              <Box sx={{ mb: 6 }}>
                <Typography 
                  variant="h4" 
                  component="h1" 
                  sx={{ 
                    mb: 1,
                    fontWeight: 'bold',
                    background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                    backgroundClip: 'text',
                    textFillColor: 'transparent',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                  }}
                >
                  Dashboard
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                  Welcome to your vaccination management dashboard
                </Typography>
              </Box>

              {/* Statistics Cards */}
              <Grid container spacing={3} sx={{ mb: 4 }}>
                <Grid item xs={12} sm={6} md={3}>
                  <StatCard
                    title="Total Students"
                    value={dashboardData.totalStudents}
                    icon={<PeopleIcon />}
                    color="#1976d2"
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <StatCard
                    title="Vaccination Rate"
                    value={`${dashboardData.statistics.vaccinationRate}%`}
                    icon={<AssessmentIcon />}
                    color="#2e7d32"
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <StatCard
                    title="Pending Vaccinations"
                    value={dashboardData.statistics.pendingVaccinations}
                    icon={<VaccinationIcon />}
                    color="#ed6c02"
                  />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                  <StatCard
                    title="Upcoming Appointments"
                    value={dashboardData.statistics.upcomingAppointments}
                    icon={<WarningIcon />}
                    color="#9c27b0"
                  />
                </Grid>
              </Grid>

              {/* Upcoming Drives and Recent Vaccinations */}
              <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                  <Paper 
                    sx={{ 
                      p: 3, 
                      height: '100%',
                      transition: 'transform 0.2s ease-in-out',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: theme.shadows[8],
                      },
                    }}
                  >
                    <Typography variant="h6" sx={{ mb: 3, fontWeight: 'bold' }}>
                      Upcoming Vaccination Drives
                    </Typography>
                    {dashboardData.upcomingDrives.length > 0 ? (
                      <List>
                        {dashboardData.upcomingDrives.map((drive, index) => (
                          <React.Fragment key={drive.id}>
                            <ListItem>
                              <ListItemText
                                primary={drive.name}
                                secondary={
                                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
                                    <CalendarIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                                    <Typography variant="body2" color="text.secondary">
                                      {new Date(drive.date).toLocaleDateString()} - {drive.location}
                                    </Typography>
                                  </Box>
                                }
                              />
                            </ListItem>
                            {index < dashboardData.upcomingDrives.length - 1 && <Divider />}
                          </React.Fragment>
                        ))}
                      </List>
                    ) : (
                      <Alert severity="info">No upcoming vaccination drives</Alert>
                    )}
                  </Paper>
                </Grid>
                <Grid item xs={12} md={6}>
                  <Paper 
                    sx={{ 
                      p: 3, 
                      height: '100%',
                      transition: 'transform 0.2s ease-in-out',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: theme.shadows[8],
                      },
                    }}
                  >
                    <Typography variant="h6" sx={{ mb: 3, fontWeight: 'bold' }}>
                      Recent Vaccinations
                    </Typography>
                    <List>
                      {dashboardData.recentVaccinations.map((vaccination, index) => (
                        <React.Fragment key={vaccination.id}>
                          <ListItem>
                            <ListItemText
                              primary={
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                  <Avatar sx={{ width: 32, height: 32, bgcolor: theme.palette.primary.main }}>
                                    {vaccination.student.charAt(0)}
                                  </Avatar>
                                  <Box>
                                    <Typography variant="subtitle1" sx={{ fontWeight: 'medium' }}>
                                      {vaccination.student}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                      {vaccination.class} • {vaccination.vaccine}
                                    </Typography>
                                  </Box>
                                </Box>
                              }
                              secondary={
                                <Box sx={{ mt: 1, display: 'flex', alignItems: 'center', gap: 1 }}>
                                  <CalendarIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                                  <Typography variant="body2" color="text.secondary">
                                    {new Date(vaccination.date).toLocaleDateString()}
                                  </Typography>
                                </Box>
                              }
                            />
                            <Chip 
                              label={vaccination.status}
                              color="success"
                              size="small"
                            />
                          </ListItem>
                          {index < dashboardData.recentVaccinations.length - 1 && <Divider />}
                        </React.Fragment>
                      ))}
                    </List>
                  </Paper>
                </Grid>
              </Grid>
            </>
          )}

          {tabValue === 1 && (
            <Box>
              <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                  <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>
                    Student Management
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Manage student records and vaccination status
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', gap: 2 }}>
                  <Button
                    variant="outlined"
                    startIcon={<FileUploadIcon />}
                    onClick={() => setOpenBulkUpload(true)}
                  >
                    Bulk Upload
                  </Button>
                  <Button
                    variant="contained"
                    startIcon={<AddIcon />}
                    sx={{
                      background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
                    }}
                  >
                    Add New Student
                  </Button>
                </Box>
              </Box>
              <StudentManagement />
            </Box>
          )}

          {tabValue === 2 && (
            <VaccinationDriveManagement />
          )}

          {tabValue === 3 && (
            <ReportGeneration />
          )}
        </Container>
      </Box>

      <BulkUploadDialog
        open={openBulkUpload}
        onClose={() => setOpenBulkUpload(false)}
      />
    </Box>
  );
}

export default Dashboard;
