function StatCard({ title, value, icon, color }) {
  const theme = useTheme();
  
  return (
    <Card 
      sx={{ 
        height: '100%',
        transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: theme.shadows[8],
        },
        background: `linear-gradient(135deg, ${alpha(color, 0.1)} 0%, ${alpha(color, 0.05)} 100%)`,
        border: `1px solid ${alpha(color, 0.2)}`,
      }}
    >
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box
            sx={{
              backgroundColor: alpha(color, 0.1),
              borderRadius: '50%',
              p: 1,
              mr: 2,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            {React.cloneElement(icon, { sx: { color, fontSize: 28 } })}
          </Box>
          <Typography variant="h6" component="div" sx={{ color: theme.palette.text.secondary }}>
            {title}
          </Typography>
        </Box>
        <Typography 
          variant="h4" 
          component="div" 
          sx={{ 
            color,
            fontWeight: 'bold',
            textAlign: 'center',
            mb: 1,
          }}
        >
          {value}
        </Typography>
      </CardContent>
    </Card>
  );
}

function TabPanel({ children, value, index }) {
  return (
    <div hidden={value !== index}>
      {value === index && <Box sx={{ p: 3 }}>{children}</Box>}
    </div>
  );
}

function StudentDetails({ student }) {
  const theme = useTheme();
  
  return (
    <Grid container spacing={3}>
      <Grid item xs={12} md={4}>
        <Paper sx={{ p: 3, height: '100%' }}>
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 3 }}>
            <Avatar 
              sx={{ 
                width: 100, 
                height: 100, 
                bgcolor: theme.palette.primary.main,
                fontSize: '2.5rem',
                mb: 2
              }}
            >
              {student.name.charAt(0)}
            </Avatar>
            <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
              {student.name}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {student.class}  Roll No: {student.rollNumber}
            </Typography>
          </Box>
          
          <Divider sx={{ my: 2 }} />
          
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <SchoolIcon color="primary" />
              <Typography variant="body2">
                Class: {student.class}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <PeopleIcon color="primary" />
              <Typography variant="body2">
                Age: {student.age} years
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <VaccinationIcon color="primary" />
              <Typography variant="body2">
                Blood Group: {student.bloodGroup}
              </Typography>
            </Box>
          </Box>

          <Divider sx={{ my: 2 }} />

          <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
            Contact Information
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <PhoneIcon fontSize="small" color="primary" />
              <Typography variant="body2">{student.contact.phone}</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <EmailIcon fontSize="small" color="primary" />
              <Typography variant="body2">{student.contact.email}</Typography>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <LocationIcon fontSize="small" color="primary" />
              <Typography variant="body2">{student.contact.address}</Typography>
            </Box>
          </Box>
        </Paper>
      </Grid>

      <Grid item xs={12} md={8}>
        <Paper sx={{ p: 3, height: '100%' }}>
          <Typography variant="h6" sx={{ mb: 3, fontWeight: 'bold' }}>
            Vaccination History
          </Typography>
          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Vaccine</TableCell>
                  <TableCell>Date</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>Next Due</TableCell>
                  <TableCell>Batch No.</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {student.vaccinations.map((vaccination, index) => (
                  <TableRow key={index}>
                    <TableCell>{vaccination.name}</TableCell>
                    <TableCell>{new Date(vaccination.date).toLocaleDateString()}</TableCell>
                    <TableCell>
                      <Chip 
                        label={vaccination.status}
                        color="success"
                        size="small"
                      />
                    </TableCell>
                    <TableCell>
                      {vaccination.nextDue === 'N/A' ? 'N/A' : new Date(vaccination.nextDue).toLocaleDateString()}
                    </TableCell>
                    <TableCell>{vaccination.batch}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          {student.medicalHistory.length > 0 && (
            <>
              <Typography variant="h6" sx={{ mt: 4, mb: 3, fontWeight: 'bold' }}>
                Medical History
              </Typography>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Condition</TableCell>
                      <TableCell>Diagnosed</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell>Notes</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {student.medicalHistory.map((condition, index) => (
                      <TableRow key={index}>
                        <TableCell>{condition.condition}</TableCell>
                        <TableCell>{condition.diagnosed}</TableCell>
                        <TableCell>
                          <Chip 
                            label={condition.status}
                            color="warning"
                            size="small"
                          />
                        </TableCell>
                        <TableCell>{condition.notes}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </>
          )}
        </Paper>
      </Grid>
    </Grid>
  );
}

function VaccinationDriveManagement() {
  const theme = useTheme();
  const [openDriveDialog, setOpenDriveDialog] = useState(false);
  const [driveFormData, setDriveFormData] = useState({
    name: '',
    date: '',
    location: '',
    totalSlots: '',
    vaccineType: '',
    batchNumber: '',
    requirements: '',
    notes: ''
  });
  const [driveFormErrors, setDriveFormErrors] = useState({});

  const handleOpenDriveDialog = () => {
    setOpenDriveDialog(true);
    setDriveFormData({
      name: '',
      date: '',
      location: '',
      totalSlots: '',
      vaccineType: '',
      batchNumber: '',
      requirements: '',
      notes: ''
    });
    setDriveFormErrors({});
  };

  const handleCloseDriveDialog = () => {
    setOpenDriveDialog(false);
  };

  const validateDriveForm = () => {
    const errors = {};
    if (!driveFormData.name.trim()) errors.name = 'Drive name is required';
    if (!driveFormData.date) errors.date = 'Date is required';
    if (!driveFormData.location.trim()) errors.location = 'Location is required';
    if (!driveFormData.totalSlots) errors.totalSlots = 'Total slots is required';
    if (!driveFormData.vaccineType.trim()) errors.vaccineType = 'Vaccine type is required';
    if (!driveFormData.batchNumber.trim()) errors.batchNumber = 'Batch number is required';

    setDriveFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleDriveInputChange = (e) => {
    const { name, value } = e.target;
    setDriveFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (driveFormErrors[name]) {
      setDriveFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleDriveSubmit = () => {
    if (validateDriveForm()) {
      const newDrive = {
        id: Date.now(),
        ...driveFormData,
        status: 'Upcoming',
        registeredStudents: 0,
        requirements: driveFormData.requirements.split(',').map(req => req.trim())
      };

      // Add to vaccination drives
      studentManagementData.vaccinationDrives.push(newDrive);

      handleCloseDriveDialog();
    }
  };

  return (
    <Box>
      <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Box>
          <Typography variant="h5" sx={{ fontWeight: 'bold', mb: 1 }}>
            Vaccination Drives
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Manage upcoming vaccination drives and registrations
          </Typography>
        </Box>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={handleOpenDriveDialog}
          sx={{
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
          }}
        >
          Add New Drive
        </Button>
      </Box>

      <Grid container spacing={3}>
        {studentManagementData.vaccinationDrives.map((drive) => (
          <Grid item xs={12} md={6} lg={4} key={drive.id}>
            <Card 
              sx={{ 
                height: '100%',
                transition: 'transform 0.2s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: theme.shadows[8],
                },
              }}
            >
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                  <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                    {drive.name}
                  </Typography>
                  <Chip 
                    label={drive.status}
                    color={
                      drive.status === 'Completed' ? 'success' :
                      drive.status === 'Upcoming' ? 'primary' :
                      'warning'
                    }
                    size="small"
                  />
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                    <CalendarIcon fontSize="small" color="primary" />
                    <Typography variant="body2">
                      {new Date(drive.date).toLocaleDateString()}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                    <LocationIcon fontSize="small" color="primary" />
                    <Typography variant="body2">
                      {drive.location}
                    </Typography>
                  </Box>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <VaccinationIcon fontSize="small" color="primary" />
                    <Typography variant="body2">
                      {drive.vaccineType}  Batch: {drive.batchNumber}
                    </Typography>
                  </Box>
                </Box>

                <Divider sx={{ my: 2 }} />

                <Box sx={{ mb: 2 }}>
                  <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
                    Registration Status
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                    <Typography variant="body2">
                      {drive.registeredStudents} / {drive.totalSlots} slots filled
                    </Typography>
                  </Box>
                  <Box sx={{ width: '100%', bgcolor: 'grey.200', borderRadius: 1, height: 8 }}>
                    <Box
                      sx={{
                        width: `${(drive.registeredStudents / drive.totalSlots) * 100}%`,
                        bgcolor: 'primary.main',
                        height: '100%',
                        borderRadius: 1,
                      }}
                    />
                  </Box>
                </Box>

                <Box>
                  <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
                    Requirements
                  </Typography>
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                    {drive.requirements.map((req, index) => (
                      <Chip
                        key={index}
                        label={req}
                        size="small"
                        variant="outlined"
                      />
                    ))}
                  </Box>
                </Box>

                {drive.notes && (
                  <Box sx={{ mt: 2 }}>
                    <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 'bold' }}>
                      Notes
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {drive.notes}
                    </Typography>
                  </Box>
                )}

                <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                  <Button
                    variant="outlined"
                    size="small"
                    startIcon={<EditIcon />}
                    fullWidth
                  >
                    Edit
                  </Button>
                  <Button
                    variant="outlined"
                    size="small"
                    startIcon={<ViewIcon />}
                    fullWidth
                  >
                    View Details
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Add New Drive Dialog */}
      <Dialog open={openDriveDialog} onClose={handleCloseDriveDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Add New Vaccination Drive</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 2 }}>
            <TextField
              label="Drive Name"
              name="name"
              value={driveFormData.name}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.name}
              helperText={driveFormErrors.name}
            />
            <TextField
              label="Date"
              name="date"
              type="date"
              value={driveFormData.date}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.date}
              helperText={driveFormErrors.date}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              label="Location"
              name="location"
              value={driveFormData.location}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.location}
              helperText={driveFormErrors.location}
            />
            <TextField
              label="Total Slots"
              name="totalSlots"
              type="number"
              value={driveFormData.totalSlots}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.totalSlots}
              helperText={driveFormErrors.totalSlots}
            />
            <TextField
              label="Vaccine Type"
              name="vaccineType"
              value={driveFormData.vaccineType}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.vaccineType}
              helperText={driveFormErrors.vaccineType}
            />
            <TextField
              label="Batch Number"
              name="batchNumber"
              value={driveFormData.batchNumber}
              onChange={handleDriveInputChange}
              fullWidth
              required
              error={!!driveFormErrors.batchNumber}
              helperText={driveFormErrors.batchNumber}
            />
            <TextField
              label="Requirements (comma-separated)"
              name="requirements"
              value={driveFormData.requirements}
              onChange={handleDriveInputChange}
              fullWidth
              multiline
              rows={2}
              placeholder="e.g., Parent consent form, Medical history"
            />
            <TextField
              label="Notes"
              name="notes"
              value={driveFormData.notes}
              onChange={handleDriveInputChange}
              fullWidth
              multiline
              rows={3}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDriveDialog}>Cancel</Button>
          <Button 
            variant="contained" 
            onClick={handleDriveSubmit}
            sx={{
              background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            }}
          >
            Add Drive
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

function StudentManagement() {
  const theme = useTheme();
  const [activeTab, setActiveTab] = useState(0);

  return (
    <Box>
      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
        <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)}>
          <Tab label="Students" />
          <Tab label="Vaccination Drives" />
        </Tabs>
      </Box>

      <TabPanel value={activeTab} index={0}>
        {/* Existing student management content */}
      </TabPanel>

      <TabPanel value={activeTab} index={1}>
        <VaccinationDriveManagement />
      </TabPanel>
    </Box>
  );
}

