import React from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Box,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  Divider,
  useTheme,
  alpha,
  Chip,
  Avatar,
} from '@mui/material';
import {
  People as PeopleIcon,
  LocalHospital as VaccinationIcon,
  Assessment as AssessmentIcon,
  Warning as WarningIcon,
  School as SchoolIcon,
  CalendarToday as CalendarIcon,
} from '@mui/icons-material';

// Sample data
const dashboardData = {
  totalStudents: 1250,
  vaccinatedStudents: 980,
  upcomingDrives: [
    { id: 1, name: 'COVID-19 Booster', date: '2024-03-15', location: 'Main Auditorium' },
    { id: 2, name: 'BCG Vaccine', date: '2024-03-20', location: 'School Health Center' },
    { id: 3, name: 'DPT Booster', date: '2024-03-25', location: 'Main Auditorium' },
  ],
  recentVaccinations: [
    { 
      id: 1, 
      student: 'Arjun Sharma',
      class: 'Class 10-A',
      age: 15,
      rollNumber: '2024A001',
      vaccine: 'COVID-19 Booster',
      date: '2024-03-01',
      status: 'Completed',
      nextDue: '2025-03-01'
    },
    { 
      id: 2, 
      student: 'Priya Patel',
      class: 'Class 9-B',
      age: 14,
      rollNumber: '2024B002',
      vaccine: 'BCG',
      date: '2024-03-02',
      status: 'Completed',
      nextDue: 'N/A'
    },
    { 
      id: 3, 
      student: 'Rahul Gupta',
      class: 'Class 11-C',
      age: 16,
      rollNumber: '2024C003',
      vaccine: 'DPT',
      date: '2024-03-03',
      status: 'Completed',
      nextDue: '2025-03-03'
    },
    { 
      id: 4, 
      student: 'Ananya Singh',
      class: 'Class 8-A',
      age: 13,
      rollNumber: '2024A004',
      vaccine: 'MMR',
      date: '2024-03-04',
      status: 'Completed',
      nextDue: 'N/A'
    },
    { 
      id: 5, 
      student: 'Vikram Mehta',
      class: 'Class 12-B',
      age: 17,
      rollNumber: '2024B005',
      vaccine: 'COVID-19 Booster',
      date: '2024-03-05',
      status: 'Completed',
      nextDue: '2025-03-05'
    },
  ],
  statistics: {
    vaccinationRate: 78.4,
    pendingVaccinations: 270,
    upcomingAppointments: 45,
    classDistribution: {
      'Class 8': 180,
      'Class 9': 220,
      'Class 10': 250,
      'Class 11': 200,
      'Class 12': 200,
    }
  }
};

function StatCard({ title, value, icon, color }) {
  const theme = useTheme();
  
  return (
    <Card 
      sx={{ 
        height: '100%',
        transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
        '&:hover': {
          transform: 'translateY(-4px)',
          boxShadow: theme.shadows[8],
        },
        background: `linear-gradient(135deg, ${alpha(color, 0.1)} 0%, ${alpha(color, 0.05)} 100%)`,
        border: `1px solid ${alpha(color, 0.2)}`,
      }}
    >
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box
            sx={{
              backgroundColor: alpha(color, 0.1),
              borderRadius: '50%',
              p: 1,
              mr: 2,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            {React.cloneElement(icon, { sx: { color, fontSize: 28 } })}
          </Box>
          <Typography variant="h6" component="div" sx={{ color: theme.palette.text.secondary }}>
            {title}
          </Typography>
        </Box>
        <Typography 
          variant="h4" 
          component="div" 
          sx={{ 
            color,
            fontWeight: 'bold',
            textAlign: 'center',
            mb: 1,
          }}
        >
          {value}
        </Typography>
      </CardContent>
    </Card>
  );
}

function Dashboard() {
  const theme = useTheme();

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 6 }}>
        <Typography 
          variant="h4" 
          component="h1" 
          sx={{ 
            mb: 1,
            fontWeight: 'bold',
            background: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
            backgroundClip: 'text',
            textFillColor: 'transparent',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
          }}
        >
          Dashboard
        </Typography>
        <Typography variant="subtitle1" color="text.secondary">
          Welcome to your vaccination management dashboard
        </Typography>
      </Box>

      {/* Statistics Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Students"
            value={dashboardData.totalStudents}
            icon={<PeopleIcon />}
            color="#1976d2"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Vaccination Rate"
            value={`${dashboardData.statistics.vaccinationRate}%`}
            icon={<AssessmentIcon />}
            color="#2e7d32"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Pending Vaccinations"
            value={dashboardData.statistics.pendingVaccinations}
            icon={<VaccinationIcon />}
            color="#ed6c02"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Upcoming Appointments"
            value={dashboardData.statistics.upcomingAppointments}
            icon={<WarningIcon />}
            color="#9c27b0"
          />
        </Grid>
      </Grid>

      {/* Upcoming Drives and Recent Vaccinations */}
      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Paper 
            sx={{ 
              p: 3, 
              height: '100%',
              transition: 'transform 0.2s ease-in-out',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: theme.shadows[8],
              },
              background: `linear-gradient(135deg, ${alpha(theme.palette.primary.main, 0.05)} 0%, ${alpha(theme.palette.primary.main, 0.02)} 100%)`,
            }}
          >
            <Typography 
              variant="h6" 
              sx={{ 
                mb: 3,
                fontWeight: 'bold',
                color: theme.palette.primary.main,
              }}
            >
              Upcoming Vaccination Drives
            </Typography>
            <List>
              {dashboardData.upcomingDrives.map((drive, index) => (
                <React.Fragment key={drive.id}>
                  <ListItem 
                    sx={{ 
                      transition: 'background-color 0.2s ease-in-out',
                      '&:hover': {
                        backgroundColor: alpha(theme.palette.primary.main, 0.05),
                        borderRadius: 1,
                      },
                    }}
                  >
                    <ListItemText
                      primary={
                        <Typography variant="subtitle1" sx={{ fontWeight: 'medium' }}>
                          {drive.name}
                        </Typography>
                      }
                      secondary={
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 0.5 }}>
                          <CalendarIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                          <Typography variant="body2" color="text.secondary">
                            {new Date(drive.date).toLocaleDateString()} - {drive.location}
                          </Typography>
                        </Box>
                      }
                    />
                  </ListItem>
                  {index < dashboardData.upcomingDrives.length - 1 && <Divider />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper 
            sx={{ 
              p: 3, 
              height: '100%',
              transition: 'transform 0.2s ease-in-out',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: theme.shadows[8],
              },
              background: `linear-gradient(135deg, ${alpha(theme.palette.secondary.main, 0.05)} 0%, ${alpha(theme.palette.secondary.main, 0.02)} 100%)`,
            }}
          >
            <Typography 
              variant="h6" 
              sx={{ 
                mb: 3,
                fontWeight: 'bold',
                color: theme.palette.secondary.main,
              }}
            >
              Recent Vaccinations
            </Typography>
            <List>
              {dashboardData.recentVaccinations.map((vaccination, index) => (
                <React.Fragment key={vaccination.id}>
                  <ListItem 
                    sx={{ 
                      transition: 'background-color 0.2s ease-in-out',
                      '&:hover': {
                        backgroundColor: alpha(theme.palette.secondary.main, 0.05),
                        borderRadius: 1,
                      },
                    }}
                  >
                    <ListItemText
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Avatar sx={{ width: 32, height: 32, bgcolor: theme.palette.primary.main }}>
                            {vaccination.student.charAt(0)}
                          </Avatar>
                          <Box>
                            <Typography variant="subtitle1" sx={{ fontWeight: 'medium' }}>
                              {vaccination.student}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                              <SchoolIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                              <Typography variant="body2" color="text.secondary">
                                {vaccination.class} • Roll No: {vaccination.rollNumber}
                              </Typography>
                            </Box>
                          </Box>
                        </Box>
                      }
                      secondary={
                        <Box sx={{ mt: 1, display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <VaccinationIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                            <Typography variant="body2" color="text.secondary">
                              {vaccination.vaccine}
                            </Typography>
                          </Box>
                          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <CalendarIcon sx={{ fontSize: 16, color: 'text.secondary' }} />
                            <Typography variant="body2" color="text.secondary">
                              {new Date(vaccination.date).toLocaleDateString()}
                              {vaccination.nextDue !== 'N/A' && ` • Next due: ${new Date(vaccination.nextDue).toLocaleDateString()}`}
                            </Typography>
                          </Box>
                        </Box>
                      }
                    />
                    <Chip 
                      label={vaccination.status}
                      color="success"
                      size="small"
                      sx={{ ml: 1 }}
                    />
                  </ListItem>
                  {index < dashboardData.recentVaccinations.length - 1 && <Divider />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
}

export default Dashboard;
